import React from 'react';
// import Signup from "./Signup";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faUser} from "@fortawesome/free-solid-svg-icons";
import {faShoppingCart} from "@fortawesome/free-solid-svg-icons";


const Header = () => {

    fetch("http://api.openweathermap.org/data/2.5/weather?q=Lviv&appid=4809b41399d85494de13a4bd92524090") 
    .then(function(resp) {return resp.json() }) 
    .then(function (data) {
        console.log(data);
        document.querySelector('.weather-city').textContent = data.name;
        document.querySelector('.weather-temperature').innerHTML = (data.main.temp - 273) + "&deg;";
    })
    .catch(function() {

    }) 
    
        return (
            
                <header style={{backgroundColor: "#3723e4",
                borderBottom: "1px solid skyblue"}}>
                    <div className="container1">
                        <div className="header__container">
                            <a href="/" className="header-logo">Wander<span>Lust</span></a>
                            <div className="header__user-container">
                            <a href="/login"className="header__login-link">
                                <FontAwesomeIcon icon={faUser} className="icon icon-user" />
                                Zaloguj się</a>
                                <div  className="weather">
                                    <p className="weather-city">Lwów</p>
                                    <p className="weather-temperature">10&deg;</p>
                                </div>
                    </div>
                    </div>
                    </div>
                </header>
            
        );
}

export default Header;
