import React from 'react';
import styled from 'styled-components';
import { Form, Button } from 'react-bootstrap';
import {Formik} from "formik";
import * as Yup from "yup";
// Styled-components styles
const CONTAINER = styled.div`
  background: #F7F9FA;
  height: auto;
  width: 90%;
  margin: 5em auto;
  color: snow;
  -webkit-box-shadow: 5px 5px 5px 0 rgba(0, 0, 0, 0.4);
  -moz-box-shadow: 5px 5px 5px 0 rgba(0, 0, 0, 0.4);
  box-shadow: 5px 5px 5px 0 rgba(0, 0, 0, 0.4);

  @media(min-width: 786px) {
    width: 60%;
  }

  label {
    color: #24B9B6;
    font-size: 1.2em;
    font-weight: 400;
  }

  h1 {
    color: #24B9B6;
    padding-top: .5em;
  }

  .form-group {
    margin-bottom: 2.5em;
  }
`;

const MYFORM = styled(Form)`
  width: 90%;
  text-align: left;
  padding-top: 2em;
  padding-bottom: 2em;

  @media(min-width: 786px) {
    width: 50%;
  }
`;

const BUTTON = styled(Button)`
  background: #1863AB;
  border: none;
  font-size: 1.2em;
  font-weight: 400;

  &:hover {
    background: #1D3461;
  }
`;

const Formularz = () => {
    const phoneRegExp = /^(\+?\d{0,4})?\s?-?\s?(\(?\d{3}\)?)\s?-?\s?(\(?\d{3}\)?)\s?-?\s?(\(?\d{4}\)?)?$/;
    
    const validationSchema = Yup.object().shape({
        name: Yup.string()
            .min(2, "*Names must have at least 2 characters")
            .max(8, "*Names can't be longer than 100 characters")
            .required("*Name is required"),
        email: Yup.string()
            .email("*Must be a valid email address")
            .max(8, "*Email must be less than 100 characters")
            .required("*Email is required"),
        phone: Yup.string()
            .required("*Phone number required"),
        blog: Yup.string()
            .url("*Must enter URL in http://www.example.com format")
            .required("*URL required")
    });
    return (
        <CONTAINER>
            <Formik initialValues={{ name:"", email:"", phone:"", blog:""}}
            validationSchema={validationSchema}>
                {( {values,
                       errors,
                       touched,
                       handleChange,
                       handleBlur,
                       handleSubmit,
                       isSubmitting }) =>(
                        <MYFORM className="mx-auto">
                            {console.log(values)}
                            <Form.Group controlId="formName">
                                <Form.Label>Name :</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="name"
                                    placeholder="Full Name"
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    value={values.name}
                                    className={touched.name && errors.name ? "error" : null}
                                />
                                {touched.name && errors.name ? (
                                    <div className="error-message">{errors.name}</div>
                                ) : null}
                            </Form.Group>
                            <Form.Group controlId="formEmail">
                                <Form.Label>Email :</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="email"
                                    placeholder="Email"
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    value={values.email}
                                />
                            </Form.Group>
                            <Form.Group controlId="formPhone">
                                <Form.Label>Phone :</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="phone"
                                    placeholder="Phone"
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    value={values.phone}
                                />
                            </Form.Group>
                            <Form.Group controlId="formBlog">
                                <Form.Label>Blog :</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="blog"
                                    placeholder="Blog URL"
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    value={values.blog}
                                />
                            </Form.Group>
                            <BUTTON variant="primary" type="submit">
                                Submit
                            </BUTTON>
                        </MYFORM>
                    )}

            </Formik>
        </CONTAINER>
    );
}


export default Formularz;
