import React, {useRef, useState} from "react";
import {Form, Button, Card, Container, Alert} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import {AuthProvider, useAuth} from "../../AuthContext";
import {Link, useHistory} from "react-router-dom";
import Header from "../home/Header";
import HeaderMain from "../home/HeaderMain";


export default function Login() {
    const emailRef = useRef()
    const passwordRef = useRef()
    // const {login} = useAuth()
    const [error, setError] = useState("")
    const [loading, setLoading] = useState(false)
    const history = useHistory()
    
    async function handleSubmit(e) {
        e.preventDefault()
    
        try {
            setError("")
            setLoading(true)
            // await login(emailRef.current.value, passwordRef.current.value)
            history.push("/")
        } catch {
            setError("Nie udało się zalogować")
        }
        setLoading(false)
    }


    return (
        <>
        <Header/>
    <HeaderMain/>
    <AuthProvider>
            <div className="d-flex align-items-center justify-content-center" style={{minHeight: "100vh"}}>
                <div className="w-100" style={{maxWidth: "400px"}}>
                    <Container>
                        <Card>
                            <Card.Body>
                                <h2 className="text-center mb-4">Zaloguj się</h2>
                                {error && <Alert variant="danger">{error}</Alert> }
                                <Form onSubmit={handleSubmit}>
                                    <Form.Group id="email">
                                        <Form.Label>Email</Form.Label>
                                        <Form.Control type="email" ref={emailRef} required/>
                                    </Form.Group>
                                    <Form.Group id="password">
                                        <Form.Label>Haslo</Form.Label>
                                        <Form.Control type="password" ref={passwordRef} required/>
                                    </Form.Group>
                                    <Button disabled={loading} className="w-100" type="submit">Log in</Button>
                                </Form>
                                <div className="w-100 text-center mt-3">
                                    <Link to="/forgot-password">Zapomniałem hasła</Link>
                                </div>
                            </Card.Body>
                        </Card>
                        <div className="w-100 text-center mt-2" style={{marginRight: "2px"}}>
                            Need an account? <Link to="/Signup">Zarejestruj się</Link>
                        </div>
                    </Container>
                </div>
            </div>
        </AuthProvider>
        </>
    )
}
